return items.map(item => {
  const words = item.json.words; // Assuming the words are stored in item.json.words
  const result = [];

  let startTime = words[0].start; // Start time for the first word
  let endTime = startTime + 6; // Set the 6-second interval

  let currentGroup = []; // Holds the current group of words

  // Loop through each word and group them into 6-second intervals
  let index = 0;
  for (const word of words) {
    if (word.start < endTime) {
      // If the word's start time is within the current 6-second interval
      currentGroup.push(word.word);
    } else {
      index ++;
      // If the word's start time exceeds the current interval, save the group
      result.push({
        text: currentGroup.join(' '),
        start: startTime,
        end: word.end,
        index: index
      });

      // Start a new group for the next interval
      startTime = word.start;
      endTime = startTime + 6;
      currentGroup = [word.word]; // Start with the current word
    }
  }

  // Push the last group if there are any remaining words
  if (currentGroup.length > 0) {
    index++;
    result.push({
      text: currentGroup.join(' '),
      start: startTime,
      end: words[words.length - 1].end,
      index: index
    });
  }

  return {
    json: {
      groups: result,
    },
  };
});

return $items('Open AI Whisper').map(item => {
  const words = item.json.words;
  const result = [];

  for (let i = 0; i < words.length; i += 3) {
    const chunk = words.slice(i, i + 3);
    result.push({
      words: chunk.map(word => word.word.charAt(0).toUpperCase() + word.word.slice(1)).join(' '),
      start: chunk[0].start,
      end: chunk[chunk.length - 1].end,
    });
  }

  return {
    json: {
      chunks: result,
    },
  };
});

