parameters.jsCode:
const items = $input.all();
const updatedItems = items.map((item) => {
  const content = item?.json?.choices[0]?.message?.content;
  try {
    const contentJson = JSON.parse(content);
    item.json.choices[0].message.content = contentJson;
  } catch (error) {
    console.log("Error parsing content to JSON:", error);
  }
  return item;
});
return updatedItems;

