// Extract sections and image prompt data from the webhook and AI response
let sections, niche, style, contentId, directories;

// First, safely attempt to get data from the expected structure
try {
  // Check different possible locations for the data
  if ($input.item.json.body && $input.item.json.body.sections) {
    // Data is in the webhook body
    sections = $input.item.json.body.sections;
    niche = $input.item.json.body.niche;
    style = $input.item.json.body.style;
    contentId = $input.item.json.body.contentId;
    directories = $input.item.json.body.directories;
  } else if ($input.first() && $input.first().json.body && $input.first().json.body.sections) {
    // Data might be in first() item if using batches
    sections = $input.first().json.body.sections;
    niche = $input.first().json.body.niche;
    style = $input.first().json.body.style;
    contentId = $input.first().json.body.contentId;
    directories = $input.first().json.body.directories;
  } else if ($input.item.json.sections) {
    // Data might be directly in the json root
    sections = $input.item.json.sections;
    niche = $input.item.json.niche;
    style = $input.item.json.style;
    contentId = $input.item.json.contentId;
    directories = $input.item.json.directories;
  } else {
    // Handle the case where data is not found
    console.log('Could not locate sections data! Input structure:', JSON.stringify($input.item.json));

    // Create fallback data
    sections = [
      {
        section_name: "fallback",
        content: "Fallback content",
        visual_prompt: "A professional, high-quality image"
      }
    ];
    niche = "tech";
    style = "informative";
    contentId = "fallback_" + Date.now();
    directories = {
      main: "/data/shared/fallback/main",
      audio: "/data/shared/fallback/audio",
      images: "/data/shared/fallback/images",
      video: "/data/shared/fallback/video",
      final: "/data/shared/fallback/final"
    };
  }
} catch (e) {
  console.log('Error accessing input data:', e.message);
  throw new Error('Could not access input data: ' + e.message);
}

// Try to get enhanced prompts from AI if available
let enhancedSections = [];
try {
  if ($input.item.json.output && $input.item.json.output.enhanced_sections) {
    enhancedSections = $input.item.json.output.enhanced_sections;
  }
} catch (e) {
  console.log('Could not find enhanced sections, using original prompts');
}

// Create image prompts for each section
const imagePrompts = sections.map((section, index) => {
  // Find enhanced prompt if available
  const enhancedSection = enhancedSections.find(es => es.index === index) || {};

  return {
    index,
    section_name: section.section_name,
    content: section.content,
    prompt: section.visual_prompt,
    enhancedPrompt: enhancedSection.enhanced_prompt || 
      `${section.visual_prompt}, ${niche} style, professional, high quality, trending, ${style}, 8k, cinematic lighting, detailed`
  };
});

// Load optimized image settings for NVIDIA 3050 GPU
const defaultImageSettings = {
  width: 768,
  height: 768,
  steps: 25,
  cfgScale: 7,
  sampler: "Euler a",  // More efficient sampler for NVIDIA GPUs
  negativePrompt: "low quality, blurry, distorted, deformed, disfigured, bad anatomy, watermark, signature, out of frame, cropped"
};

return {
  contentId,
  directories,
  niche,
  style,
  imagePrompts,
  imageSettings: defaultImageSettings,
  totalImages: imagePrompts.length,
  completedImages: 0
};

