// Extract the image data from the response
const responseData = $input.item.json;
let imageData;

// Check if the expected path exists, otherwise look for alternatives
if (responseData.body && responseData.body.images && responseData.body.images.length > 0) {
  imageData = responseData.body.images[0];
} else if (responseData.images && responseData.images.length > 0) {
  imageData = responseData.images[0];
} else {
  // Create a 1x1 transparent pixel as fallback
  imageData = "R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7";
  console.log("Using fallback image - no image data found in response");
}

// Get index from batch processing, but ensure it's unique
const index = $input.item.json.index || 0;
const sectionName = $input.item.json.section_name || `section_${index}`;

// Add timestamp to ensure uniqueness if needed
const timestamp = Date.now();
const uniqueSuffix = timestamp.toString().slice(-4);
const formattedIndex = index.toString().padStart(2, '0');

// Ensure filename is unique by adding timestamp suffix
const uniqueFilename = `image_${formattedIndex}_${uniqueSuffix}`;

// Decode base64 image
const imageBuffer = Buffer.from(imageData, 'base64');

// Return with binary data and unique filename
return [{
  json: {
    ...responseData,
    currentIndex: index,
    sectionName: sectionName,
    uniqueFilename: uniqueFilename
  },
  binary: {
    data: {
      data: imageBuffer,
      mimeType: 'image/png',
      fileName: `${uniqueFilename}.png`
    }
  }
}];

// Create a placeholder image for failed generations
// Get the index and format with leading zeros
const index = $input.item.json.index || 0;
const formattedIndex = index.toString().padStart(2, '0');
const sectionName = $input.item.json.section_name || `section_${index}`;

// Create a 1x1 transparent pixel as a placeholder image
const placeholderBase64 = "R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7";
const imageBuffer = Buffer.from(placeholderBase64, 'base64');

// Return with binary data format matching the successful case
return [{
  json: {
    ...$input.item.json,
    currentIndex: index,
    sectionName: sectionName,
    imageGenerationFailed: true
  },
  binary: {
    data: {
      data: imageBuffer,
      mimeType: 'image/png',
      fileName: `placeholder_${formattedIndex}.png`
    }
  }
}];

// Increment the completed images counter
let completedImages = $input.item.json.completedImages || 0;
completedImages++;

// Check if we've completed all images
const allImagesCompleted = completedImages >= $input.item.json.totalImages;

// Add metadata about the image
const imagesMetadata = $input.item.json.imagesMetadata || [];
imagesMetadata.push({
  index: $input.item.json.currentIndex,
  path: $input.item.json.imagePath,
  sectionName: $input.item.json.sectionName,
  failed: $input.item.json.imageGenerationFailed || false
});

return {
  ...$input.item.json,
  completedImages,
  allImagesCompleted,
  imagesMetadata
};

// Create completion file to signal visual generation completion
const directories = $node["Visual Generation Webhook"].json.body.directories;
const contentId = $node["Visual Generation Webhook"].json.body.contentId || $input.item.json.contentId;
const completionPath = `${directories.main}/visual_complete.json`;

// Use the metadata we collected to create a more complete report
// Get image paths using the same path pattern as used in the workflow
const totalImages = $input.item.json.totalImages || 0;
const imageFiles = [];

// Generate the image file paths
for (let i = 0; i < totalImages; i++) {
  const formattedIndex = i.toString().padStart(2, '0');
  imageFiles.push(`${directories.images}/image_${formattedIndex}.png`);
}

const completionData = {
  contentId: contentId,
  visualComplete: true,
  totalImages: totalImages,
  imageFiles: imageFiles,
  timestamp: new Date().toISOString()
};

return {
  ...$input.item.json,
  completionPath: completionPath,
  completionData: JSON.stringify(completionData, null, 2),
  visualComplete: true
};

// Log generation summary
const contentId = $node["Visual Generation Webhook"].json.body.contentId || $input.item.json.contentId;
const totalImages = $input.item.json.totalImages || 0;
const completedImages = $input.item.json.completedImages || 0;

// Determine how many images failed if that data is available
let failedImages = 0;
if ($input.item.json.imagesMetadata) {
  failedImages = $input.item.json.imagesMetadata.filter(img => img.failed).length;
} else {
  // Estimate failures based on completed vs total
  failedImages = Math.max(0, totalImages - completedImages);
}

const summary = {
  contentId: contentId,
  totalImagesRequested: totalImages,
  imagesSuccessfullyGenerated: completedImages - failedImages,
  imagesFailed: failedImages,
  generationCompleted: new Date().toISOString()
};

// Create formatted log message
const logMessage = `
VISUAL GENERATION SUMMARY
------------------------
Content ID: ${summary.contentId}
Total Images: ${summary.totalImagesRequested}
Successful: ${summary.imagesSuccessfullyGenerated}
Failed: ${summary.imagesFailed}
Completed at: ${summary.generationCompleted}
`;

console.log(logMessage);

return {
  ...summary,
  ...($input.item.json)
};

// Function Node to properly check Stable Diffusion API response
const responseData = $input.item.json;
let success = false;

// Log the response structure for debugging
console.log("Response structure:", JSON.stringify(responseData, null, 2).substring(0, 500) + "...");

// Check if the images array exists and has at least one item
if (responseData.body && 
    responseData.body.images && 
    Array.isArray(responseData.body.images) && 
    responseData.body.images.length > 0) {
  success = true;
  console.log("Success: Found images array with data");
} 
// Fallback check for different response structures
else if (responseData.images && 
         Array.isArray(responseData.images) && 
         responseData.images.length > 0) {
  success = true;  
  console.log("Success: Found top-level images array");
}
// Check for error indicators
else if (responseData.error || 
        (responseData.body && responseData.body.error)) {
  success = false;
  console.log("Error detected in response");
}
// If nothing else helps, check if we have any data at all
else {
  console.log("No clear success or failure indicators found");
  // Default to success if we have any kind of response data
  success = Object.keys(responseData).length > 0;
}

// Return the original data plus our success flag
return {
  ...responseData,
  imageGenerationSuccess: success
};
